substitutions.substitution:
$a = rand(3, 6);
$content = "{";
for ($x = 1; $x <= $a; $x++) {
   $val = rand(1,6);
   $content .= $val;
   if($x < $a) {
       $content .= ", ";
   }
   else {
       $content .= "}";
   }
} 

return $content;
